scripts/migrate.ts.content:
import { drizzle } from 'drizzle-orm/neon-serverless';
import { migrate } from 'drizzle-orm/neon-serverless/migrator';
import { Pool } from '@neondatabase/serverless';

async function main() {
  const pool = new Pool({ connectionString: process.env.DATABASE_URL });
  const db = drizzle(pool);

  console.log('Running migrations...');
  await migrate(db, { migrationsFolder: 'drizzle' });
  console.log('Migrations completed!');

  await pool.end();
}

main().catch(console.error);

server/db.ts.content:
import { drizzle } from "drizzle-orm/neon-serverless";
import { Pool } from "@neondatabase/serverless";
import { users, properties, contacts, propertyInquiries } from '../shared/schema.js';

let db: any;
let isDbConnected = false;

try {
  if (process.env.DATABASE_URL) {
    const isProduction = process.env.NODE_ENV === 'production';

    // Use pooler URL for production with SSL
    let connectionString = process.env.DATABASE_URL;
    if (isProduction && !connectionString.includes('-pooler.')) {
      connectionString = connectionString.replace('.us-east-2', '-pooler.us-east-2');
    }

    const poolConfig = { 
      connectionString,
      max: isProduction ? 3 : 10,
      connectionTimeoutMillis: 15000,
      idleTimeoutMillis: 30000,
    };

    // Add SSL only for production
    if (isProduction) {
      poolConfig.ssl = { rejectUnauthorized: false };
    }

    const pool = new Pool(poolConfig);

    db = drizzle(pool, { 
      schema: { users, properties, contacts, propertyInquiries } 
    });
    isDbConnected = true;
    console.log('‚úÖ Database connected successfully');
  } else {
    console.log('‚ö†Ô∏è DATABASE_URL not found, using memory storage');
    isDbConnected = false;
  }
} catch (error) {
  console.error('‚ùå Database connection failed:', error);
  isDbConnected = false;
}

export { db, isDbConnected };

server/index.ts.content:
import express, { type Request } from "express";
import type { Response, NextFunction } from "express";
import { registerRoutes } from "./routes.js";
import { setupVite, serveStatic, log } from "./vite.js";

const app = express();
app.use(express.json());
app.use(express.urlencoded({ extended: false }));

app.use((req, res, next) => {
  const start = Date.now();
  const path = req.path;
  let capturedJsonResponse: Record<string, any> | undefined = undefined;

  const originalResJson = res.json;
  res.json = function (bodyJson, ...args) {
    capturedJsonResponse = bodyJson;
    return originalResJson.apply(res, [bodyJson, ...args]);
  };

  res.on("finish", () => {
    const duration = Date.now() - start;
    if (path.startsWith("/api")) {
      let logLine = `${req.method} ${path} ${res.statusCode} in ${duration}ms`;
      if (capturedJsonResponse) {
        logLine += ` :: ${JSON.stringify(capturedJsonResponse)}`;
      }

      if (logLine.length > 80) {
        logLine = logLine.slice(0, 79) + "‚Ä¶";
      }

      log(logLine);
    }
  });

  next();
});

(async () => {
  const server = await registerRoutes(app);

  app.use((err: any, _req: Request, res: Response, _next: NextFunction) => {
    const status = err.status || err.statusCode || 500;
    const message = err.message || "Internal Server Error";

    res.status(status).json({ message });
    throw err;
  });

  // importantly only setup vite in development and after
  // setting up all the other routes so the catch-all route
  // doesn't interfere with the other routes
  if (app.get("env") === "development") {
    await setupVite(app, server);
  } else {
    serveStatic(app);
  }

  // ALWAYS serve the app on the port specified in the environment variable PORT
  // Other ports are firewalled. Default to 5000 if not specified.
  // this serves both the API and the client.
  // It is the only port that is not firewalled.
  const port = parseInt(process.env.PORT || "5000", 10);

  server.listen(port, "0.0.0.0", () => {
    log(`üöÄ Server running on port ${port} in ${process.env.NODE_ENV || 'development'} mode`);
  });

  // Graceful shutdown
  const gracefulShutdown = () => {
    console.log('üîÑ Graceful shutdown initiated...');
    server.close(() => {
      console.log('‚úÖ Server closed successfully');
      process.exit(0);
    });
  };

  process.on('SIGTERM', gracefulShutdown);
  process.on('SIGINT', gracefulShutdown);

  // Handle uncaught exceptions
  process.on('uncaughtException', (err) => {
    console.error('üí• Uncaught Exception:', err);
    process.exit(1);
  });

  process.on('unhandledRejection', (reason, promise) => {
    console.error('üí• Unhandled Rejection at:', promise, 'reason:', reason);
    process.exit(1);
  });
})();

