  --ring: hsl(240, 4.9%, 83.9%);
  --chart-1: hsl(220, 70%, 50%);
  --chart-2: hsl(160, 60%, 45%);
  --chart-3: hsl(30, 80%, 55%);
  --chart-4: hsl(280, 65%, 60%);
  --chart-5: hsl(340, 75%, 55%);
}

@layer base {
  * {
    @apply border-border;
  }
  body {
    @apply bg-background text-foreground;
    font-family: 'Inter', sans-serif;
  }
  /* Favicon */
  link[rel="icon"] {
    content: url('/public/favicon.png');
  }
}

@layer utilities {
  /* Contact Section Icons */
  .contact-icon {
    width: 24px;
    height: 24px;
    display: inline-block;
    vertical-align: middle;
    margin-right: 8px;
    background-size: contain;
    background-repeat: no-repeat;
    background-position: center;
  }

  .icon-office {
    background-image: url('/icons/building.svg'); /* Replace with your office icon */
  }

  .icon-phone {
    background-image: url('/icons/phone.svg'); /* Replace with your phone icon */
  }

  .icon-email {
    background-image: url('/icons/mail.svg'); /* Replace with your email icon */
  }

  /* Text Colors */
  .text-ebutine-blue {
    color: var(--ebutine-blue);
  }
  .text-ebutine-blue-light {
    color: var(--ebutine-blue-light);
  }
  .text-ebutine-orange {
    color: var(--ebutine-orange);
  }
  .text-ebutine-dark {
    color: var(--ebutine-dark);
  }

  /* Background Colors */
  .bg-ebutine-blue {
    background-color: var(--ebutine-blue);
  }
  .bg-ebutine-orange {
    background-color: var(--ebutine-orange);
  }
  .bg-ebutine-dark {
    background-color: var(--ebutine-dark);
  }
  .bg-ebutine-light {
    background-color: var(--ebutine-light);
  }

  /* Gradient Backgrounds */
  .bg-gradient-ebutine {
    background: linear-gradient(135deg, var(--ebutine-blue) 0%, var(--ebutine-dark) 100%);
  }
  .bg-gradient-ebutine-orange {
    background: linear-gradient(135deg, var(--ebutine-orange) 0%, var(--ebutine-orange-dark) 100%);
  }
  .bg-gradient-warm {
    background: linear-gradient(135deg, rgb(254, 202, 202) 0%, rgb(252, 165, 165) 50%, var(--ebutine-orange) 100%);
  }
  .bg-gradient-cool {
    background: linear-gradient(135deg, var(--ebutine-light) 0%, rgb(219, 234, 254) 50%, var(--ebutine-blue) 100%);
  }

  /* Border Colors */
  .border-ebutine-orange {
    border-color: var(--ebutine-orange);
  }
  .border-ebutine-blue {
    border-color: var(--ebutine-blue);
  }

  /* Hover Effects */
  .hover\:bg-ebutine-orange:hover {
    background-color: var(--ebutine-orange);
  }
  .hover\:text-ebutine-orange:hover {
    color: var(--ebutine-orange);
  }
  .hover\:shadow-orange:hover {
    box-shadow: var(--shadow-orange);
  }
  .hover\:shadow-blue:hover {
    box-shadow: var(--shadow-blue);
  }

  /* Enhanced Shadows */
  .shadow-ebutine {
    box-shadow: var(--shadow-xl);
  }
  .shadow-ebutine-orange {
    box-shadow: var(--shadow-orange);
  }
  .shadow-ebutine-blue {
    box-shadow: var(--shadow-blue);
  }

  /* Glass Effect */
  .glass {
    background: rgba(255, 255, 255, 0.25);
    backdrop-filter: blur(10px);
    -webkit-backdrop-filter: blur(10px);
    border: 1px solid rgba(255, 255, 255, 0.18);
  }

  /* Enhanced Transitions */
  .transition-all-smooth {
    transition: all 0.3s cubic-bezier(0.4, 0, 0.2, 1);
  }

  /* Button Enhancements */
  .btn-primary {
    @apply bg-gradient-ebutine-orange text-white font-semibold py-3 px-8 rounded-xl shadow-lg hover:shadow-ebutine-orange transition-all-smooth transform hover:scale-105;
  }

  .btn-secondary {
    @apply bg-white text-ebutine-blue border-2 border-ebutine-blue font-semibold py-3 px-8 rounded-xl shadow-lg hover:bg-ebutine-blue hover:text-white transition-all-smooth;
  }

  /* Card Enhancements */
  .card-enhanced {
    @apply bg-white rounded-2xl shadow-lg hover:shadow-ebutine transition-all-smooth transform hover:-translate-y-2;
  }

  /* Typography Enhancements */
  .text-gradient {
    background: linear-gradient(135deg, var(--ebutine-blue) 0%, var(--ebutine-orange) 100%);
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
    background-clip: text;
  }

  .text-shadow-strong {
    text-shadow: 2px 2px 8px rgba(0, 0, 0, 0.7), 1px 1px 4px rgba(0, 0, 0, 0.5);
  }

  /* Enhanced Button Styles */
  .btn-primary {
    @apply bg-ebutine-orange hover:bg-orange-600 text-white font-semibold py-3 px-8 rounded-xl shadow-lg hover:shadow-ebutine-orange transition-all-smooth transform hover:scale-105;
  }

  .btn-secondary {
    @apply bg-white text-ebutine-blue border-2 border-ebutine-blue font-semibold py-3 px-8 rounded-xl shadow-lg hover:bg-ebutine-blue hover:text-white transition-all-smooth;
  }

  .btn-outline {
    @apply bg-transparent border-2 border-white text-white hover:bg-white hover:text-ebutine-dark font-semibold py-3 px-8 rounded-xl shadow-lg backdrop-blur-sm transition-all-smooth;
  }

  /* Ready to Invest section styling */
  .ready-to-invest {
    background: white !important;
    color: #2c3e50 !important;
  }

  /* Ensure all button text is always visible */
  .ready-to-invest .btn-primary,
  .ready-to-invest button {
    background: #f39c12 !important;
    color: white !important;
    border: none !important;
  }

  .ready-to-invest .btn-secondary,
  .ready-to-invest button[variant="outline"],
  .ready-to-invest a button,
  .ready-to-invest a[class*="border"] {
    background: #2c3e50 !important;
    color: white !important;
    border: 2px solid #2c3e50 !important;
  }

  .ready-to-invest .btn-secondary:hover,
  .ready-to-invest button[variant="outline"]:hover,
  .ready-to-invest a button:hover,
  .ready-to-invest a[class*="border"]:hover {
    background: #34495e !important;
    color: white !important;
    border: 2px solid #34495e !important;
  }

  /* Specific styling for all second buttons including WhatsApp, View Properties, etc. */
  .ready-to-invest a[href*="whatsapp"] button,
  .ready-to-invest a[href*="wa.me"] button,
  .ready-to-invest a[href*="tel:"] button,
  .ready-to-invest a[href*="properties"] button,
  .ready-to-invest a[href*="/properties"] button,
  .ready-to-invest a button[variant="outline"],
  .ready-to-invest a .border-white,
  .ready-to-invest a .border-2 {
    background: #2c3e50 !important;
    color: white !important;
    border: 2px solid #2c3e50 !important;
  }

  .ready-to-invest a[href*="whatsapp"] button:hover,
  .ready-to-invest a[href*="wa.me"] button:hover,
  .ready-to-invest a[href*="tel:"] button:hover,
  .ready-to-invest a[href*="properties"] button:hover,
  .ready-to-invest a[href*="/properties"] button:hover,
  .ready-to-invest a button[variant="outline"]:hover,
  .ready-to-invest a .border-white:hover,
  .ready-to-invest a .border-2:hover {
    background: #34495e !important;
    color: white !important;
    border: 2px solid #34495e !important;
  }

  /* Footer Social Icons Removal */
  .footer-social-icons a[href*="linkedin"],
  .footer-social-icons a[href*="youtube"] {
    display: none;
  }
}

