import { useState } from "react";
import { useMutation, useQueryClient } from "@tanstack/react-query";
import { zodResolver } from "@hookform/resolvers/zod";
import { useForm } from "react-hook-form";
import { Button } from "@/components/ui/button";
import { Form, FormControl, FormField, FormItem, FormLabel, FormMessage } from "@/components/ui/form";
import { Input } from "@/components/ui/input";
import { Textarea } from "@/components/ui/textarea";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { RadioGroup, RadioGroupItem } from "@/components/ui/radio-group";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";
import { insertContactSchema, type InsertContact } from "@shared/schema";
import { Send } from "lucide-react";

export default function ContactForm() {
  const { toast } = useToast();
  const queryClient = useQueryClient();

  const form = useForm<InsertContact>({
    resolver: zodResolver(insertContactSchema),
    defaultValues: {
      fullName: "",
      email: "",
      phone: "",
      location: "",
      propertyType: "",
      budget: "",
      purpose: "",
      message: "",
      contactMethod: "",
    },
  });

  const mutation = useMutation({
    mutationFn: async (data: InsertContact) => {
      return apiRequest("POST", "/api/contacts", data);
    },
    onSuccess: () => {
      toast({
        title: "Message Sent Successfully!",
        description: "Thank you for your inquiry. We will contact you within 24 hours.",
      });
      form.reset();
      queryClient.invalidateQueries({ queryKey: ["/api/contacts"] });
    },
    onError: (error) => {
      toast({
        title: "Error Sending Message",
        description: "There was an error sending your message. Please try again.",
        variant: "destructive",
      });
      console.error("Contact form error:", error);
    },
  });

  const onSubmit = (data: InsertContact) => {
    mutation.mutate(data);
  };

  return (
    <div className="card-enhanced p-8">
      <h3 className="text-3xl font-bold text-gradient mb-8">Send us a Message</h3>

      <Form {...form}>
        <form onSubmit={form.handleSubmit(onSubmit)} className="space-y-6" data-testid="contact-form">
          {/* Personal Information */}
          <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
            <FormField
              control={form.control}
              name="fullName"
              render={({ field }) => (
                <FormItem>
                  <FormLabel>Full Name *</FormLabel>
                  <FormControl>
                    <Input placeholder="Enter your full name" {...field} data-testid="input-full-name" />
                  </FormControl>
                  <FormMessage />
                </FormItem>
              )}
            />
            <FormField
              control={form.control}
              name="email"
              render={({ field }) => (
                <FormItem>
                  <FormLabel>Email Address *</FormLabel>
                  <FormControl>
                    <Input type="email" placeholder="Enter your email" {...field} data-testid="input-email" />
                  </FormControl>
                  <FormMessage />
                </FormItem>
              )}
            />
          </div>

          <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
            <FormField
              control={form.control}
              name="phone"
              render={({ field }) => (
                <FormItem>
                  <FormLabel>Phone Number *</FormLabel>
                  <FormControl>
                    <Input type="tel" placeholder="+234 xxx xxx xxxx" {...field} data-testid="input-phone" />
                  </FormControl>
                  <FormMessage />
                </FormItem>
              )}
            />
            <FormField
              control={form.control}
              name="location"
              render={({ field }) => (
                <FormItem>
                  <FormLabel>Current Location</FormLabel>
                  <Select onValueChange={field.onChange} defaultValue={field.value || ""}>
                    <FormControl>
                      <SelectTrigger data-testid="select-location">
                        <SelectValue placeholder="Select Location" />
                      </SelectTrigger>
                    </FormControl>
                    <SelectContent>
                      <SelectItem value="Nigeria">Nigeria</SelectItem>
                      <SelectItem value="United Kingdom">United Kingdom</SelectItem>
                      <SelectItem value="United States">United States</SelectItem>
                      <SelectItem value="Canada">Canada</SelectItem>
                      <SelectItem value="Australia">Australia</SelectItem>
                      <SelectItem value="Germany">Germany</SelectItem>
                      <SelectItem value="Other">Other</SelectItem>
                    </SelectContent>
                  </Select>
                  <FormMessage />
                </FormItem>
              )}
            />
          </div>

          {/* Investment Preferences */}
          <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
            <FormField
              control={form.control}
              name="propertyType"
              render={({ field }) => (
                <FormItem>
                  <FormLabel>Property Type Interest</FormLabel>
                  <Select onValueChange={field.onChange} defaultValue={field.value || ""}>
                    <FormControl>
                      <SelectTrigger data-testid="select-property-type">
                        <SelectValue placeholder="Select Property Type" />
                      </SelectTrigger>
                    </FormControl>
                    <SelectContent>
                      <SelectItem value="residential_land">Residential Land</SelectItem>
                      <SelectItem value="commercial_land">Commercial Land</SelectItem>
                      <SelectItem value="luxury_home">Luxury Homes</SelectItem>
                      <SelectItem value="mixed">Mixed Portfolio</SelectItem>
                    </SelectContent>
                  </Select>
                  <FormMessage />
                </FormItem>
              )}
            />
            <FormField
              control={form.control}
              name="budget"
              render={({ field }) => (
                <FormItem>
                  <FormLabel>Budget Range</FormLabel>
                  <Select onValueChange={field.onChange} defaultValue={field.value || ""}>
                    <FormControl>
                      <SelectTrigger data-testid="select-budget">
                        <SelectValue placeholder="Select Budget Range" />
                      </SelectTrigger>
                    </FormControl>
                    <SelectContent>
                      <SelectItem value="5-15">‚Ç¶5M - ‚Ç¶15M</SelectItem>
                      <SelectItem value="15-30">‚Ç¶15M - ‚Ç¶30M</SelectItem>
                      <SelectItem value="30-50">‚Ç¶30M - ‚Ç¶50M</SelectItem>
                      <SelectItem value="50-100">‚Ç¶50M - ‚Ç¶100M</SelectItem>
                      <SelectItem value="100+">‚Ç¶100M+</SelectItem>
                    </SelectContent>
                  </Select>
                  <FormMessage />
                </FormItem>
              )}
            />
          </div>

          <FormField
            control={form.control}
            name="purpose"
            render={({ field }) => (
              <FormItem>
                <FormLabel>Investment Purpose</FormLabel>
                <Select onValueChange={field.onChange} defaultValue={field.value || ""}>
                  <FormControl>
                    <SelectTrigger data-testid="select-purpose">
                      <SelectValue placeholder="Select Purpose" />
                    </SelectTrigger>
                  </FormControl>
                  <SelectContent>
                    <SelectItem value="personal">Personal Use / Relocation</SelectItem>
                    <SelectItem value="investment">Investment / Capital Appreciation</SelectItem>
                    <SelectItem value="rental">Rental Income Generation</SelectItem>
                    <SelectItem value="development">Property Development</SelectItem>
                    <SelectItem value="portfolio">Portfolio Diversification</SelectItem>
                  </SelectContent>
                </Select>
                <FormMessage />
              </FormItem>
            )}
          />

          <FormField
            control={form.control}
            name="message"
            render={({ field }) => (
              <FormItem>
                <FormLabel>Message</FormLabel>
                <FormControl>
                  <Textarea 
                    placeholder="Tell us about your property requirements, questions, or how we can help you..."
                    rows={5}
                    {...field}
                    value={field.value || ""}
                    data-testid="textarea-message"
                  />
                </FormControl>
                <FormMessage />
              </FormItem>
            )}
          />

          {/* Preferred Contact Method */}
          <FormField
            control={form.control}
            name="contactMethod"
            render={({ field }) => (
              <FormItem>
                <FormLabel>Preferred Contact Method</FormLabel>
                <FormControl>
                  <RadioGroup
                    onValueChange={field.onChange}
                    defaultValue={field.value || ""}
                    className="grid grid-cols-1 md:grid-cols-3 gap-4"
                    data-testid="radio-contact-method"
                  >
                    <div className="flex items-center space-x-2 p-3 border border-gray-300 rounded-lg">
                      <RadioGroupItem value="email" id="email" />
                      <label htmlFor="email" className="text-ebutine-blue cursor-pointer">Email</label>
                    </div>
                    <div className="flex items-center space-x-2 p-3 border border-gray-300 rounded-lg">
                      <RadioGroupItem value="phone" id="phone" />
                      <label htmlFor="phone" className="text-ebutine-blue cursor-pointer">Phone Call</label>
                    </div>
                    <div className="flex items-center space-x-2 p-3 border border-gray-300 rounded-lg">
                      <RadioGroupItem value="whatsapp" id="whatsapp" />
                      <label htmlFor="whatsapp" className="text-ebutine-blue cursor-pointer">WhatsApp</label>
                    </div>
                  </RadioGroup>
                </FormControl>
                <FormMessage />
              </FormItem>
            )}
          />

          <Button 
            type="submit" 
            className="btn-primary w-full py-4 px-8 text-lg"
            disabled={mutation.isPending}
            data-testid="submit-contact-form"
          >
            {mutation.isPending ? (
              "Sending..."
            ) : (
              <>
                <Send className="mr-2 h-4 w-4" />
                Send Message
              </>
            )}
          </Button>

          <p className="text-xs text-ebutine-blue text-center">
            By submitting this form, you agree to our privacy policy and consent to be contacted by Ebutine Properties.
          </p>
        </form>
      </Form>
    </div>
  );
}

client/src/components/FeaturedProperties.content:
import { useQuery } from "@tanstack/react-query";
import PropertyCard from "./PropertyCard";
import { Button } from "@/components/ui/button";
import { Link } from "wouter";
import type { Property } from "@shared/schema";

export default function FeaturedProperties() {
  const { data: properties = [], isLoading } = useQuery<Property[]>({
    queryKey: ["/api/properties"],
    queryFn: async () => {
      const response = await fetch("/api/properties?featured=true");
      if (!response.ok) throw new Error("Failed to fetch properties");
      return response.json();
    },
  });

  const handleViewDetails = (id: string) => {
    // Navigate to property details page
    window.location.href = `/properties/${id}`;
  };

  const handleInquire = (id: string) => {
    // Open WhatsApp with property inquiry
    const message = `Hi, I'm interested in the property with ID: ${id}. Can you provide more details?`;
    window.open(`https://wa.me/2349061461411?text=${encodeURIComponent(message)}`, '_blank');
  };

  if (isLoading) {
    return (
      <section className="py-20 bg-white">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="text-center mb-16">
            <h2 className="text-4xl font-bold text-ebutine-dark mb-4">Featured Properties</h2>
            <p className="text-xl text-ebutine-blue max-w-2xl mx-auto">
              Handpicked premium properties with verified documentation and clear titles
            </p>
          </div>
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-8">
            {[1, 2, 3].map((i) => (
              <div key={i} className="bg-gray-200 rounded-2xl h-96 animate-pulse"></div>
            ))}
          </div>
        </div>
      </section>
    );
  }

  return (
    <section className="py-20 bg-white">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        <div className="text-center mb-16">
          <h2 className="text-4xl font-bold text-ebutine-dark mb-4">Featured Properties</h2>
          <p className="text-xl text-ebutine-blue max-w-2xl mx-auto">
            Handpicked premium properties with verified documentation and clear titles
          </p>
        </div>

        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-8" data-testid="featured-properties-grid">
          {properties.map((property) => (
            <PropertyCard
              key={property.id}
              property={property}
              onViewDetails={handleViewDetails}
              onInquire={handleInquire}
            />
          ))}
        </div>

        <div className="text-center mt-12">
          <Link href="/properties">
            <Button 
              className="bg-ebutine-blue hover:bg-gray-600 text-white font-semibold py-4 px-8 text-lg"
              data-testid="view-all-properties-button"
            >
              View All Properties
            </Button>
          </Link>
        </div>
      </div>
    </section>
  );
}

client/src/components/HeroSection.tsx.content:
import { useState, useEffect } from "react";
import { Link } from "wouter";
import { Search, ChevronLeft, ChevronRight } from "lucide-react";
import { Button } from "@/components/ui/button";
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from "@/components/ui/select";

export default function HeroSection() {
  const [searchFilters, setSearchFilters] = useState({
    propertyType: "",
    location: "",
    budget: "",
  });

  const [currentSlide, setCurrentSlide] = useState(0);

  const slides = [
    {
      image:
        "https://images.unsplash.com/photo-1564013799919-ab600027ffc6?ixlib=rb-4.0.3&auto=format&fit=crop&w=1920&h=1080",
      title: "Luxury Living Redefined",
      subtitle:
        "Discover premium residential properties in Lagos's most prestigious neighborhoods",
      highlight: "Luxury Homes",
    },
    {
      image:
        "https://images.unsplash.com/photo-1580587771525-78b9dba3b914?ixlib=rb-4.0.3&auto=format&fit=crop&w=1920&h=1080",
      title: "Your Dream Home Awaits",
      subtitle:
        "Beautiful residential lands with verified documentation and clear titles",
      highlight: "Residential Land",
    },
    {
      image:
        "https://images.unsplash.com/photo-1560518883-ce09059eeffa?ixlib=rb-4.0.3&auto=format&fit=crop&w=1920&h=1080",
      title: "Investment Opportunities",
      subtitle:
        "Strategic commercial properties in high-growth areas across Lagos",
      highlight: "Commercial Land",
    },
    {
      image:
        "https://images.unsplash.com/photo-1582407947304-fd86f028f716?ixlib=rb-4.0.3&auto=format&fit=crop&w=1920&h=1080",
      title: "Verified Properties Only",
      subtitle:
        "Every property undergoes rigorous verification for your peace of mind",
      highlight: "100% Verified",
    },
  ];

  useEffect(() => {
    const timer = setInterval(() => {
      setCurrentSlide((prev) => (prev + 1) % slides.length);
    }, 6000);
    return () => clearInterval(timer);
  }, []);

  const nextSlide = () => {
    setCurrentSlide((prev) => (prev + 1) % slides.length);
  };

  const prevSlide = () => {
    setCurrentSlide((prev) => (prev - 1 + slides.length) % slides.length);
  };

  const handleSearch = () => {
    const params = new URLSearchParams();
    if (searchFilters.propertyType)
      params.set("type", searchFilters.propertyType);
    if (searchFilters.location) params.set("location", searchFilters.location);
    if (searchFilters.budget) params.set("budget", searchFilters.budget);

    window.location.href = `/properties?${params.toString()}`;
  };

  return (
    <section className="relative h-screen overflow-hidden">
      {/* Carousel Background */}
      <div className="absolute inset-0">
        {slides.map((slide, index) => (
          <div
            key={index}
            className={`absolute inset-0 transition-all duration-1000 ease-in-out transform ${
              index === currentSlide
                ? "opacity-100 scale-100"
                : "opacity-0 scale-110"
            }`}
          >
            <div
              style={{
                backgroundImage: `url('${slide.image}')`,
                backgroundSize: "cover",
                backgroundPosition: "center",
                backgroundAttachment: "fixed",
              }}
              className="absolute inset-0"
            ></div>
            <div className="absolute inset-0 bg-black/60"></div>
            <div className="absolute inset-0 bg-gradient-to-br from-ebutine-dark/80 via-ebutine-blue/70 to-ebutine-dark/90"></div>
          </div>
        ))}
      </div>

      {/* Floating Animation Elements */}
      <div className="absolute inset-0 pointer-events-none overflow-hidden">
        <div className="absolute top-20 left-10 opacity-20 animate-float-slow">
          <div className="bg-white/20 rounded-2xl p-6 backdrop-blur-sm transform rotate-12">
