import { useRoute, useLocation } from 'wouter';
import { useQuery } from "@tanstack/react-query";
import { Helmet } from "react-helmet";
import { ArrowLeft, Heart, Phone, MapPin, Bed, Bath, Car, Calendar, Eye, Share2 } from "lucide-react";
import { Button } from "@/components/ui/button";
import { Card, CardContent } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Carousel, CarouselContent, CarouselItem, CarouselNext, CarouselPrevious } from "@/components/ui/carousel";
import { useState } from "react";
import PropertyVideoModal from "@/components/PropertyVideoModal";
import type { Property } from "@shared/schema";

export default function PropertyDetails() {
  const [match, params] = useRoute(`/properties/:id`);
  const id = params?.id;
  const [, setLocation] = useLocation();
  const [showVideoModal, setShowVideoModal] = useState(false);

  const navigate = (path: string) => setLocation(path);

  const { data: property, isLoading, error } = useQuery<Property>({
    queryKey: [`/api/properties/${id}`],
    queryFn: async () => {
      const response = await fetch(`/api/properties/${id}`);
      if (!response.ok) {
        if (response.status === 404) {
          throw new Error("Property not found");
        }
        throw new Error("Failed to fetch property");
      }
      return response.json();
    },
    enabled: !!id,
  });

  if (isLoading) {
    return (
      <div className="min-h-screen bg-ebutine-light flex items-center justify-center">
        <div className="text-center">
          <div className="animate-spin rounded-full h-32 w-32 border-b-2 border-ebutine-orange mx-auto mb-4"></div>
          <p className="text-ebutine-blue">Loading property details...</p>
        </div>
      </div>
    );
  }

  if (error || !property) {
    return (
      <div className="min-h-screen bg-ebutine-light flex items-center justify-center">
        <div className="text-center">
          <div className="text-6xl mb-4">üè†</div>
          <h1 className="text-2xl font-bold text-ebutine-dark mb-4">Property Not Found</h1>
          <p className="text-ebutine-blue mb-8">The property you're looking for doesn't exist or has been removed.</p>
          <Button onClick={() => navigate('/properties')} className="bg-ebutine-orange hover:bg-orange-600">
            <ArrowLeft className="mr-2 h-4 w-4" />
            Back to Properties
          </Button>
        </div>
      </div>
    );
  }

  const formatPrice = (price: string) => {
    const num = parseFloat(price);
    if (num >= 1000000) {
      return `‚Ç¶${(num / 1000000).toFixed(1)}M`;
    }
    return `‚Ç¶${num.toLocaleString()}`;
  };

  const handleInquire = () => {
    const message = `Hi, I'm interested in "${property.title}" located in ${property.location}, priced at ${formatPrice(property.price)}. Can you provide more details?`;
    window.open(`https://wa.me/2349061461411?text=${encodeURIComponent(message)}`, '_blank');
  };

  const handleShare = () => {
    if (navigator.share) {
      navigator.share({
        title: property.title,
        text: `Check out this property: ${property.title}`,
        url: window.location.href,
      });
    } else {
      navigator.clipboard.writeText(window.location.href);
    }
  };

  const allMedia = [
    ...(property.images || []).map(img => ({ type: 'image', src: img })),
    ...(property.videos || []).map(video => ({ type: 'video', src: video })),
    ...(property.embedCodes || []).map((embed, index) => ({ type: 'embed', src: embed.embedCode, title: embed.title || `Embed ${index + 1}` }))
  ];

  return (
    <div className="min-h-screen bg-ebutine-light">
      <Helmet>
        <title>{property.title} - Ebutine Properties</title>
        <meta name="description" content={property.description} />
      </Helmet>

      {/* Header */}
      <div className="bg-white border-b">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-4">
          <div className="flex items-center justify-between">
            <Button variant="ghost" onClick={() => navigate('/properties')}>
              <ArrowLeft className="mr-2 h-4 w-4" />
              Back to Properties
            </Button>
            <div className="flex space-x-2">
              <Button variant="outline" size="sm" onClick={handleShare}>
                <Share2 className="h-4 w-4" />
              </Button>
              <Button variant="outline" size="sm">
                <Heart className="h-4 w-4" />
              </Button>
            </div>
          </div>
        </div>
      </div>

      {/* Property Images/Videos Carousel */}
      <div className="bg-white">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
          {allMedia.length > 0 ? (
            <Carousel className="w-full">
              <CarouselContent>
                {allMedia.map((media, index) => (
                  <CarouselItem key={index}>
                    <div className="relative h-96 md:h-[500px] rounded-2xl overflow-hidden flex items-center justify-center">
                      {media.type === 'image' ? (
                        <img
                          src={media.src}
                          alt={`${property.title} - Image ${index + 1}`}
                          className="w-full h-full object-cover"
                        />
                      ) : media.type === 'video' ? (
                        <video
                          src={media.src}
                          className="w-full h-full object-cover"
                          controls
                          preload="metadata"
                        />
                      ) : (
                        <div 
                          className="w-full h-full flex items-center justify-center"
                          dangerouslySetInnerHTML={{ __html: media.src }}
                        />
                      )}
                    </div>
                  </CarouselItem>
                ))}
              </CarouselContent>
              <CarouselPrevious />
              <CarouselNext />
            </Carousel>
          ) : (
            <div className="h-96 md:h-[500px] rounded-2xl bg-gray-200 flex items-center justify-center">
              <p className="text-gray-500">No images available</p>
            </div>
          )}
        </div>
      </div>

      {/* Property Details */}
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        <div className="grid grid-cols-1 lg:grid-cols-3 gap-8">
          {/* Main Content */}
          <div className="lg:col-span-2 space-y-6">
            <Card>
              <CardContent className="p-6">
                <div className="flex items-center justify-between mb-4">
                  <Badge className={
                    property.status === "available" ? "bg-green-500" :
                    property.status === "sold" ? "bg-red-500" : "bg-blue-500"
                  }>
                    {property.status}
                  </Badge>
                  {property.featured && (
                    <Badge className="bg-ebutine-orange">Featured</Badge>
                  )}
                </div>

                <h1 className="text-3xl font-bold text-ebutine-dark mb-4">{property.title}</h1>

                <div className="flex items-center text-ebutine-blue mb-6">
                  <MapPin className="h-5 w-5 mr-2" />
                  <span>{property.location}</span>
                </div>

                <div className="mb-6">
                  <p className="text-4xl font-bold text-gradient mb-2">
                    {formatPrice(property.price)}
                  </p>
                  <p className="text-ebutine-blue-light">Starting price</p>
                </div>

                {/* Property Stats */}
                <div className="grid grid-cols-2 md:grid-cols-4 gap-4 mb-6">
                  {property.bedrooms && (
                    <div className="text-center p-4 bg-ebutine-light rounded-lg">
                      <Bed className="h-6 w-6 mx-auto mb-2 text-ebutine-orange" />
                      <p className="font-semibold">{property.bedrooms}</p>
                      <p className="text-sm text-ebutine-blue">Bedrooms</p>
                    </div>
                  )}
                  {property.bathrooms && (
                    <div className="text-center p-4 bg-ebutine-light rounded-lg">
                      <Bath className="h-6 w-6 mx-auto mb-2 text-ebutine-orange" />
                      <p className="font-semibold">{property.bathrooms}</p>
                      <p className="text-sm text-ebutine-blue">Bathrooms</p>
                    </div>
                  )}
                  {property.parking && (
                    <div className="text-center p-4 bg-ebutine-light rounded-lg">
                      <Car className="h-6 w-6 mx-auto mb-2 text-ebutine-orange" />
                      <p className="font-semibold">{property.parking}</p>
                      <p className="text-sm text-ebutine-blue">Parking</p>
                    </div>
                  )}
                  {property.size && (
                    <div className="text-center p-4 bg-ebutine-light rounded-lg">
                      <Eye className="h-6 w-6 mx-auto mb-2 text-ebutine-orange" />
                      <p className="font-semibold">{property.size}</p>
                      <p className="text-sm text-ebutine-blue">Size</p>
                    </div>
                  )}
                </div>

                {/* Description */}
                <div>
                  <h2 className="text-xl font-bold text-ebutine-dark mb-4">Description</h2>
                  <p className="text-ebutine-blue leading-relaxed">{property.description}</p>
                </div>
              </CardContent>
            </Card>

            {/* Features */}
            {property.features && property.features.length > 0 && (
              <Card>
                <CardContent className="p-6">
                  <h2 className="text-xl font-bold text-ebutine-dark mb-4">Features & Amenities</h2>
                  <div className="grid grid-cols-1 md:grid-cols-2 gap-3">
                    {property.features.map((feature, index) => (
                      <div key={index} className="flex items-center">
                        <div className="h-2 w-2 bg-ebutine-orange rounded-full mr-3"></div>
                        <span className="text-ebutine-blue">{feature}</span>
                      </div>
                    ))}
                  </div>
                </CardContent>
              </Card>
            )}
          </div>

          {/* Sidebar */}
          <div className="space-y-6">
            {/* Contact Card */}
            <Card className="sticky top-6">
              <CardContent className="p-6">
                <h3 className="text-lg font-bold text-ebutine-dark mb-4">Interested in this property?</h3>

                <div className="space-y-4">
                  <Button 
                    className="w-full bg-ebutine-orange hover:bg-orange-600"
                    onClick={handleInquire}
                  >
                    <Phone className="mr-2 h-4 w-4" />
                    Contact Agent
                  </Button>

                  {((property.videos && property.videos.length > 0) || 
                    (property.externalVideos && property.externalVideos.length > 0) ||
                    (property.embedCodes && property.embedCodes.length > 0)) && (
                    <Button 
                      variant="outline" 
                      className="w-full"
                      onClick={() => setShowVideoModal(true)}
                    >
                      <Eye className="mr-2 h-4 w-4" />
                      Watch Property Tour
                    </Button>
                  )}
                </div>

                <div className="mt-6 pt-6 border-t">
                  <div className="text-center">
                    <p className="text-sm text-ebutine-blue mb-2">Need financing assistance?</p>
                    <Button variant="outline" size="sm">
                      Get Pre-approved
                    </Button>
                  </div>
                </div>
              </CardContent>
            </Card>
          </div>
        </div>
      </div>

      {/* Video Modal */}
      <PropertyVideoModal
        isOpen={showVideoModal}
        onClose={() => setShowVideoModal(false)}
        videos={property.videos || []}
        externalVideos={property.externalVideos || []}
        embedCodes={property.embedCodes || []}
        propertyTitle={property.title}
      />
    </div>
  );
}

client/src/pages/PropertyListings.tsx.content:
import { useState } from "react";
import { useQuery } from "@tanstack/react-query";
import { Helmet } from "react-helmet";
import { Search, SlidersHorizontal, ChevronLeft, ChevronRight } from "lucide-react";
import { Button } from "@/components/ui/button";
import { Card, CardContent } from "@/components/ui/card";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import PropertyCard from "@/components/PropertyCard";
import type { Property } from "@shared/schema";

export default function PropertyListings() {
  const [filters, setFilters] = useState({
    propertyType: "all",
    location: "all",
    priceRange: "all",
    bedrooms: "all",
    purpose: "all",
  });

  const [currentPage, setCurrentPage] = useState(1);
  const propertiesPerPage = 9;

  // Parse URL params on component mount
  const [isInitialized, setIsInitialized] = useState(false);

  useState(() => {
    if (!isInitialized) {
      const urlParams = new URLSearchParams(window.location.search);
      const initialFilters = {
        propertyType: urlParams.get("type") || "all",
        location: urlParams.get("location") || "all",
        priceRange: urlParams.get("budget") || "all",
        bedrooms: "all",
        purpose: "all",
      };
      setFilters(initialFilters);
      setIsInitialized(true);
    }
  });

  const { data: properties = [], isLoading } = useQuery<Property[]>({
    queryKey: ["/api/properties", filters],
    queryFn: async () => {
      const params = new URLSearchParams();
      if (filters.propertyType && filters.propertyType !== "all") {
        params.set("type", filters.propertyType);
      }
      if (filters.location && filters.location !== "all") {
        params.set("location", filters.location);
      }

      const response = await fetch(`/api/properties?${params.toString()}`);
      if (!response.ok) throw new Error("Failed to fetch properties");
      return response.json();
    },
  });

  // Filter properties client-side for additional filters not handled by API
  const filteredProperties = properties.filter(property => {
    if (filters.priceRange && filters.priceRange !== "all") {
      const price = parseFloat(property.price);
      const [min, max] = filters.priceRange.includes("+")
        ? [parseFloat(filters.priceRange.replace(/[^\d]/g, "")), Infinity]
        : filters.priceRange.split("-").map(p => parseFloat(p.replace(/[^\d]/g, "")) * 1000000);

      if (price < min || price > max) return false;
    }

    if (filters.bedrooms && filters.bedrooms !== "all" && property.bedrooms) {
      const bedCount = parseInt(property.bedrooms);
      if (filters.bedrooms === "1-2" && (bedCount < 1 || bedCount > 2)) return false;
      if (filters.bedrooms === "3-4" && (bedCount < 3 || bedCount > 4)) return false;
      if (filters.bedrooms === "5+" && bedCount < 5) return false;
    }

    return true;
  });

  const totalPages = Math.ceil(filteredProperties.length / propertiesPerPage);
  const paginatedProperties = filteredProperties.slice(
    (currentPage - 1) * propertiesPerPage,
    currentPage * propertiesPerPage
  );

  const handleFilterChange = (filterType: string, value: string) => {
    setFilters(prev => ({ ...prev, [filterType]: value }));
    setCurrentPage(1); // Reset to first page when filters change
  };

  const handleViewDetails = (id: string) => {
    // Navigate to property details page
    window.location.href = `/properties/${id}`;
  };

  const handleInquire = (id: string) => {
    const property = properties.find(p => p.id === id);
    const message = `Hi, I'm interested in ${property?.title} (${property?.location}) priced at ${property?.price}. Can you provide more details?`;
    window.open(`https://wa.me/2349061461411?text=${encodeURIComponent(message)}`, '_blank');
  };

  return (
    <div>
      <Helmet>
        <title>Property Listings - Buy Land & Luxury Homes in Lagos | Ebutine Properties</title>
        <meta name="description" content="Browse verified property listings in Lagos, Nigeria. Residential land, commercial properties, luxury homes with clear titles. Trusted by diaspora clients." />
        <meta name="keywords" content="buy land Lagos, residential land Nigeria, commercial property Lagos, luxury homes Lagos, verified properties Nigeria" />
      </Helmet>

      {/* Hero Section */}
      <section className="py-20 bg-gradient-to-br from-ebutine-dark to-ebutine-blue text-white">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 text-center">
          <h1 className="text-4xl md:text-6xl font-bold mb-6">Property Listings</h1>
          <p className="text-xl md:text-2xl text-gray-200 max-w-3xl mx-auto">
            Browse our extensive collection of verified properties across Lagos
          </p>
        </div>
      </section>

      {/* Filters Section */}
      <section className="py-12 bg-gradient-to-br from-gray-50 to-white border-b">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <Card className="shadow-2xl border-0 bg-white/80 backdrop-blur-sm">
            <CardContent className="p-8">
              <div className="flex items-center justify-between mb-6">
                <h2 className="text-2xl font-bold text-ebutine-dark flex items-center">
                  <SlidersHorizontal className="mr-3 h-6 w-6" />
                  Filter Properties
                </h2>
                <div className="text-sm text-ebutine-blue">
                  {filteredProperties.length} properties found
                </div>
              </div>

              <div className="grid grid-cols-1 md:grid-cols-3 lg:grid-cols-6 gap-4">
                <div>
                  <label className="block text-sm font-medium text-ebutine-dark mb-2">Property Type</label>
                  <Select value={filters.propertyType} onValueChange={(value) => handleFilterChange("propertyType", value)}>
                    <SelectTrigger data-testid="filter-property-type">
                      <SelectValue placeholder="All Types" />
                    </SelectTrigger>
                    <SelectContent>
                      <SelectItem value="all">All Types</SelectItem>
                      <SelectItem value="residential_land">Residential Land</SelectItem>
                      <SelectItem value="commercial_land">Commercial Land</SelectItem>
                      <SelectItem value="luxury_home">Luxury Homes</SelectItem>
                      <SelectItem value="apartment">Apartments</SelectItem>
                    </SelectContent>
                  </Select>
                </div>

                <div>
                  <label className="block text-sm font-medium text-ebutine-dark mb-2">Location</label>
                  <Select value={filters.location} onValueChange={(value) => handleFilterChange("location", value)}>
                    <SelectTrigger data-testid="filter-location">
                      <SelectValue placeholder="All Locations" />
                    </SelectTrigger>
                    <SelectContent>
                      <SelectItem value="all">All Locations</SelectItem>
                      <SelectItem value="Ajah">Ajah</SelectItem>
                      <SelectItem value="Sangotedo">Sangotedo</SelectItem>
                      <SelectItem value="Ibeju-Lekki">Ibeju-Lekki</SelectItem>
                      <SelectItem value="Victoria Island">Victoria Island</SelectItem>
                      <SelectItem value="Lekki">Lekki</SelectItem>
                      <SelectItem value="Ikeja">Ikeja</SelectItem>
                    </SelectContent>
                  </Select>
                </div>

                <div>
                  <label className="block text-sm font-medium text-ebutine-dark mb-2">Price Range</label>
                  <Select value={filters.priceRange} onValueChange={(value) => handleFilterChange("priceRange", value)}>
                    <SelectTrigger data-testid="filter-price-range">
                      <SelectValue placeholder="Any Price" />
                    </SelectTrigger>
                    <SelectContent>
                      <SelectItem value="all">Any Price</SelectItem>
                      <SelectItem value="5-15">‚Ç¶5M - ‚Ç¶15M</SelectItem>
                      <SelectItem value="15-30">‚Ç¶15M - ‚Ç¶30M</SelectItem>
                      <SelectItem value="30-50">‚Ç¶30M - ‚Ç¶50M</SelectItem>
                      <SelectItem value="50-100">‚Ç¶50M - ‚Ç¶100M</SelectItem>
                      <SelectItem value="100+">‚Ç¶100M+</SelectItem>
                    </SelectContent>
                  </Select>
                </div>

                <div>
                  <label className="block text-sm font-medium text-ebutine-dark mb-2">Bedrooms</label>
                  <Select value={filters.bedrooms} onValueChange={(value) => handleFilterChange("bedrooms", value)}>
                    <SelectTrigger data-testid="filter-bedrooms">
                      <SelectValue placeholder="Any" />
                    </SelectTrigger>
                    <SelectContent>
                      <SelectItem value="all">Any</SelectItem>
                      <SelectItem value="1-2">1-2 Bedrooms</SelectItem>
                      <SelectItem value="3-4">3-4 Bedrooms</SelectItem>
                      <SelectItem value="5+">5+ Bedrooms</SelectItem>
                    </SelectContent>
                  </Select>
                </div>

                <div>
                  <label className="block text-sm font-medium text-ebutine-dark mb-2">Purpose</label>
                  <Select value={filters.purpose} onValueChange={(value) => handleFilterChange("purpose", value)}>
                    <SelectTrigger data-testid="filter-purpose">
                      <SelectValue placeholder="Any Purpose" />
                    </SelectTrigger>
                    <SelectContent>
                      <SelectItem value="all">Any Purpose</SelectItem>
                      <SelectItem value="investment">Investment</SelectItem>
                      <SelectItem value="personal">Personal Use</SelectItem>
                      <SelectItem value="rental">Rental Income</SelectItem>
                      <SelectItem value="development">Development</SelectItem>
                    </SelectContent>
                  </Select>
                </div>

                <div className="flex items-end">
                  <Button
                    className="w-full bg-ebutine-orange hover:bg-orange-600 text-white font-semibold"
                    onClick={() => {
                      // Filters are applied automatically via state changes
                      console.log("Filters applied:", filters);
                    }}
                    data-testid="apply-filters-button"
                  >
                    <Search className="mr-2 h-4 w-4" />
                    Apply Filters
                  </Button>
                </div>
              </div>
            </CardContent>
          </Card>
        </div>
      </section>

      {/* Properties Grid */}
      <section className="py-20 bg-ebutine-light">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          {isLoading ? (
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-8">
              {Array.from({ length: 6 }).map((_, i) => (
                <div key={i} className="bg-gray-200 rounded-2xl h-96 animate-pulse"></div>
              ))}
            </div>
          ) : filteredProperties.length === 0 ? (
            <div className="text-center py-20">
              <div className="text-6xl mb-4">üè†</div>
              <h3 className="text-2xl font-bold text-ebutine-dark mb-4">No Properties Found</h3>
              <p className="text-ebutine-blue mb-8">
                No properties match your current search criteria. Try adjusting your filters or browse all properties.
              </p>
              <Button
                onClick={() => {
                  setFilters({
                    propertyType: "all",
                    location: "all",
                    priceRange: "all",
                    bedrooms: "all",
                    purpose: "all",
                  });
                  setCurrentPage(1);
                }}
                className="bg-ebutine-orange hover:bg-orange-600 text-white"
              >
                Clear All Filters
              </Button>
            </div>
          ) : (
            <>
              <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-8 mb-12" data-testid="properties-grid">
                {paginatedProperties.map((property) => (
                  <PropertyCard
                    key={property.id}
                    property={property}
                    onViewDetails={handleViewDetails}
                    onInquire={handleInquire}
                  />
                ))}
              </div>

              {/* Pagination */}
              {totalPages > 1 && (
                <div className="flex justify-center items-center space-x-4" data-testid="pagination">
                  <Button
                    variant="outline"
                    onClick={() => setCurrentPage(prev => Math.max(prev - 1, 1))}
                    disabled={currentPage === 1}
                    data-testid="previous-page-button"
                  >
                    <ChevronLeft className="mr-2 h-4 w-4" />
                    Previous
                  </Button>

                  <div className="flex space-x-2">
                    {Array.from({ length: Math.min(totalPages, 5) }, (_, i) => {
                      const pageNum = i + 1;
                      return (
                        <Button
                          key={pageNum}
                          variant={currentPage === pageNum ? "default" : "outline"}
                          className={currentPage === pageNum ? "bg-ebutine-orange hover:bg-orange-600" : ""}
                          onClick={() => setCurrentPage(pageNum)}
                          data-testid={`page-button-${pageNum}`}
                        >
                          {pageNum}
                        </Button>
                      );
                    })}
                  </div>

                  <Button
                    variant="outline"
                    onClick={() => setCurrentPage(prev => Math.min(prev + 1, totalPages))}
                    disabled={currentPage === totalPages}
                    data-testid="next-page-button"
                  >
                    Next
                    <ChevronRight className="ml-2 h-4 w-4" />
                  </Button>
                </div>
              )}
            </>
          )}
        </div>
      </section>

      {/* CTA Section */}
      <section className="py-20 ready-to-invest">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 text-center">
          <h2 className="text-4xl font-bold mb-6">Ready to Invest?</h2>
          <p className="text-xl text-gray-600 mb-8 max-w-2xl mx-auto">
            Get personalized property recommendations from our experts
          </p>

          <div className="flex flex-col sm:flex-row justify-center items-center space-y-4 sm:space-y-0 sm:space-x-6">
            <Button size="lg" className="bg-ebutine-orange hover:bg-orange-600 text-white font-semibold py-4 px-8 text-lg">
              <a href="/contact">Request Custom Search</a>
            </Button>
            <Button variant="outline" size="lg" className="border-2 border-white text-white hover:bg-white hover:text-ebutine-dark font-semibold py-4 px-8 text-lg">
              <a href="https://wa.me/2349061461411" target="_blank" rel="noopener noreferrer">
                <i className="fab fa-whatsapp mr-2"></i>Chat on WhatsApp
              </a>
            </Button>
          </div>
        </div>
      </section>
    </div>
  );
}

